mapper.select:
select bt.id,title,content,writer,bct.name as categoryName,bt.category as categoryID, date,hit from board_tb as bt left join board_category_tb as bct on bt.category = bct.id where del = 0
and bct.id = #{categoryID}
ORDER BY bt.id DESC limit #{startPage},10

choose.otherwise:
select bct.id as id,bct.name as name, count(bt.content) as count from board_category_tb AS bct
left join board_tb as bt
on bct.id = bt.category
where bct.id = #{value}
group by name;
